import React, { useState } from "react";
import { Container, Row, Col } from "reactstrap";

function SectionIntroduction() {
  const [currentPhase, setCurrentPhase] = useState(1); // Current phase is 1

  // Function to render the phase with bold text only for the current phase
  const renderPhase = (phaseNumber, title, description) => (
    <div className="roadmap-phase">
      <h5 style={{ fontWeight: phaseNumber === currentPhase ? 'bold' : 'normal' }}>
        {title}
      </h5>
      <p>{description}</p>
    </div>
  );

  return (
    <>
      <div className="section section-roadmap">
        <Container className="text-center">
          <Row>
            <Col md="12">
              <h2 className="title">Welcome to GhostWithToast</h2>
              <p className="description">
                Discover the thrilling world of GhostWithToast, where gaming meets blockchain technology.
                Dive into our roadmap to see how we‚Äôre enhancing the gaming experience with innovative features
                and community-driven developments.
              </p>
            </Col>
          </Row>
          <Row>
            <Col className="ml-auto mr-auto" md="10">
              <h3 className="title">Explore the Roadmap</h3>
              <Row>
                <Col md="6">
                  {renderPhase(1, "üåü Phase 1: Community Building and Initial Marketing", "Build a strong community foundation and create buzz around the project.")}
                  {renderPhase(2, "üë• Phase 2: Token Development and Strategic Partnerships", "Develop the tokenomics and establish key partnerships.")}
                  {renderPhase(3, "üîç Phase 3: Prototype Development and Feedback", "Begin development on simplified game prototypes and gather user feedback.")},
                  {renderPhase(4, "üì¢ Phase 4: Expanded Marketing and Community Events", "Ramp up marketing efforts and host community events.")}
                </Col>
                <Col md="6">
                  {renderPhase(5, "üöÄ Phase 5: Full Game Development and Beta Testing", "Develop full versions of selected games and conduct extensive beta testing.")}
                  {renderPhase(6, "üéÆ Phase 6: Official Launch and Scaling", "Launch the games publicly and scale the platform.")}
                  {renderPhase(7, "üíé Phase 7: Sustainability and Expansion", "Ensure long-term engagement and expand the platform‚Äôs features.")}
                </Col>
              </Row>
            </Col>
          </Row>
        </Container>
      </div>
    </>
  );
}

export default SectionIntroduction;
